import merge from './merge'

/**
 * Loops through object and executes the callback function.
 * @param {Object} object 
 * @param {function} cb
 * @param {Object} options
 * @param {?string} [options.pass="both"] Choose what arguments to pass to callback. (`values`, `keys` or `both`)
 * @param {?boolean} [options.fromRight=false] If true, starts the loop from right (in a reversed order).
 */
export default function(object, cb, options){
    options = merge(options, {
        fromRight: false,
        pass: 'both'
    })
    let keys = Object.keys(object)
    if (options.fromRight) keys = keys.reverse()
    keys.forEach(key => {
        let value = object[key], args;
        switch (options.pass.trim().toUpperCase()){
            case 'KEYS': 
                args = [key]
                break
            case 'VALUES': 
                args = [value]
                break
            default:
                args = [value, key]
        }
        cb.apply(null, args)
    })
}

import parsePath from './parsePath'

/**
 * Gets from object by path.
 * @param {Object} object 
 * @param {string} path 
 * @returns {*}
 */
export default function(object, path) {
    path = parsePath(path)
    let t = 0, res;
    path.forEach(e => {
        res = (t === 0 ? object : res)[e]
        t++
    })
    return res
}

/**
 * Determines if object has the given key(s).
 * @param {Object} object 
 * @param {any} key Could be everything: String, an array of strings, or a function that returns a boolean.
 * @returns {boolean}
 */

export default function(object, key){
	if (Array.isArray(key)){
        return Object.keys(object).filter(e => key.includes(e)).every(e => e in object)
    }
	else if (typeof key === 'function') {
		let res = []
		Object.entries(object).forEach(e => {
			let [keyName, value] = e,
			returnedFromCB = key.apply(null, [keyName, value])
			res.push(returnedFromCB)
		})
		return !res.length ? false : !!res.find(Boolean)
	}
	else return key in object
}

/**
 * Checks if the given argument is an object.
 * @param {Object} obj
 */
export default obj => !!obj && typeof obj === 'object' && !Array.isArray(obj)

/** 
 * Merges given objects with no recursion.
 * @param {...Object} objects 
 * @returns {Object}
*/

export default function(...objects){
	let res = {}
	objects.forEach(e => {
		Object.assign(res, e)
	})
	return res
}

import is from './is'

/**
 * Merges with recursion. (deep merge)
 * @param {Object} target 
 * @param {Object} source 
 * @returns {Object}
 */

export default function md(target, source) {
    let res = target
    for (const key in source) {
        if (is(source[key])) {
            if (!target[key]) Object.assign(res, { [key]: {} });
            md(target[key], source[key]);
        } else {
            Object.assign(res, { [key]: source[key] });
        }
    }
    return res
}

/**
 * Gives you only the wanted keys.
 * @param {Object} object 
 * @param {(string|string[])} keys 
 * @returns {Object}
 */

export default function(object, keys){
	if (typeof keys === 'undefined' || 
		keys === '*' || 
		(Array.isArray(keys) && !keys.length) ||
		keys === true) return object
	if (typeof keys === 'string') keys = [keys]

	const res = {}
	Object.keys(object).filter(e => keys.includes(e)).forEach(e => { res[e] = object[e] })
	return res
}

/**
 * Parses the path to an array.
 * @param {string} path 
 * @returns {string[]} Parsed path
 */
export default function(path){
	path = path.replace(/\[(.+?)\]/g, ($, $1) => {
		return '.' + $1.replace(/["'`]/g, '')
	})
	return path.split('.')
}
