/**
 * Determines if `object` has the given key(s).
 * @param {object} object 
 * @param {any} key 
 * @returns {boolean} A boolean
 */

export default function(object, key){
	// more options will come (like functions, regular expressions, ...)
	if (Array.isArray(key)) return Object.keys(object).filter(e => key.includes(e)).every(e => e in object)
	else return key in object
}

import merge from './merge'
import only from './only'
import has from './has'
/* import mergeAll from './mergeAll' */

export default { merge, only, has }

/** 
 * Merges given objects with no recursion.
 * @param {object[]} objects
 * @returns object
*/

export default function merge(...objects){
	let res = {}
	objects.forEach(e => {
		Object.assign(res, e)
	})
	return res
}

import merge from './merge'

/**
 * Merges with recursion.
 * @param {object} target 
 * @param {object} source 
 */
function mergeAll(target, source){
	for (const key in source) {
		if (source.hasOwnProperty(key)) {
			let sel = source[key],
			tel = target[key];

			while (typeof sel === 'object' && typeof tel === 'object'){
				sel = merge(sel, tel)
			}
			target[key] = sel
		}
	}
	return target
}

export default mergeAll
