/**
 * Determines if `object` has the given key(s).
 * @param {Object} object 
 * @param {any} key Could be everything: String, an array of strings, or a function that returns a boolean.
 * @returns {boolean}
 */

export default function(object, key){
	if (Array.isArray(key)) return Object.keys(object).filter(e => key.includes(e)).every(e => e in object)
	else if (typeof key === 'function') {
		let res = []
		Object.entries(object).forEach(e => {
			let [keyName, value] = e,
			returnedFromCB = key.apply(null, [keyName, value])
			res.push(returnedFromCB)
		})
		return !res.length ? false : !!res.find(Boolean)
	}
	else return key in object
}

/** 
 * Merges given objects with no recursion.
 * @param {...Object} objects 
 * @returns {Object}
*/

export default function(...objects){
	let res = {}
	objects.forEach(e => {
		Object.assign(res, e)
	})
	return res
}

import merge from './merge'
import has from './has'

/**
 * Merges with recursion.
 * @param {Object} target 
 * @param {Object} source 
 */
function mergeAll(target, source){
	for (const key in source) {
		if (source.hasOwnProperty(key)) {
			let sel = source[key],
			tel = target[key];

			if (typeof sel === 'object' && typeof tel === 'object' && has(sel, (k, v) => typeof v === 'object')){
				sel = merge(sel, tel)
			}
			target[key] = sel
		}
	}
	return target
}

export default mergeAll

/**
 * Gives you only the wanted keys.
 * @param {Object} object 
 * @param {(string|string[])} keys 
 * @returns {Object}
 */

export default function(object, keys){
	if (typeof keys === 'undefined' || 
		keys === '*' || 
		(Array.isArray(keys) && !keys.length) ||
		keys === true) return object
	if (typeof keys === 'string') keys = [keys]

	const res = {}
	Object.keys(object).filter(e => keys.includes(e)).forEach(e => { res[e] = object[e] })
	return res
}

/**
 * Parses the path to an array.
 * @param {string)} path 
 * @returns {string[]} Parsed path
 */
export default function(path){
	path = path.replace(/\[(.+?)\]/g, ($, $1) => {
		return '.' + $1.replace(/["'`]/g, '')
	})
	return path.split('.')
}
